import React, { Component } from 'react';
import { DatePicker, Button } from 'antd';

export default class componentName extends Component {
    state = {
        title: '',
        description: '',
        isRadio: 1,
        anonymous: 2,
        dateTime: null,
        searchVal: '',
        option: [],
    };
    render() {
        const { anonymous, isRadio, title, description, option, searchVal } = this.state;
        return (
            <div>
                <li>
                    标题：
                    <input
                        type="text"
                        placeholder="请输入标题"
                        value={title}
                        onChange={e => this.setState({ title: e.target.value })}
                    />
                </li>
                <li>
                    描述：
                    <input
                        type="text"
                        placeholder="请输入描述"
                        value={description}
                        onChange={e => this.setState({ description: e.target.value })}
                    />
                </li>
                <li>
                    是否匿名：
                    <select
                        value={anonymous}
                        onChange={e => this.setState({ anonymous: e.target.value })}
                    >
                        <option value="1">匿名</option>
                        <option value="0">署名</option>
                    </select>
                </li>
                <li>
                    单/多选：
                    <select
                        value={isRadio}
                        onChange={e => this.setState({ isRadio: e.target.value })}
                    >
                        <option value="1">单选</option>
                        <option value="0">多选</option>
                    </select>
                </li>
                <li>
                    截止日期：
                    <DatePicker
                        onChange={dateString =>
                            this.setState({ dateTime: new Date(dateString).getTime() })
                        }
                        showTime={true}
                    />
                </li>
                <li>
                    选项：
                    <div>
                        {option.map(item => {
                            return (
                                <p>
                                    {item.name} <span>-</span>
                                </p>
                            );
                        })}
                    </div>
                    <p>
                        <input
                            type="text"
                            value={searchVal}
                            onChange={e => this.setState({ searchVal: e.target.value })}
                        />
                        <span onClick={this.headleAdd.bind(this)}>+</span>
                    </p>
                </li>
                <li>
                    <Button>提交</Button>
                </li>
            </div>
        );
    }
    headleAdd() {
        let { searchVal, option } = this.state;
        option.push({
            id: new Date().getTime() + option.length,
            name: searchVal,
        });
        this.setState({
            option: [...option],
        });
    }
}
